    public static function menage_add_follower()
    {

        $request = file_get_contents("php://input");
        $data = json_decode($request, true);
        if ($_SERVER['REQUEST_METHOD'] === 'POST') {

            $response = array();
            $thin_app_id = isset($data['thin_app_id']) ? $data['thin_app_id'] : "";
