            $data = json_decode($request, true);
            $return = false;
            $method = $_SERVER['REQUEST_METHOD'];
        }

        if ($method === 'POST') {

            $response = array();
            $thin_app_id = isset($data['thin_app_id']) ? $data['thin_app_id'] : "";
            $app_key = isset($data['app_key']) ? $data['app_key'] : "";
            $user_id = isset($data['user_id']) ? $data['user_id'] : "";
