                        $response['status'] = 0;
                        $response['message'] = "Sorry payment could not add";
                    }
                } else {
                    $connection->rollback();
                    $response['status'] = 0;
                    $response['message'] = "Sorry payment could not add";
                }
            }
            if ($return_type === true) {
                return json_encode($response);
            } else {
                Custom::sendResponse($response);
            }
        }
        exit();
    }

