                            } else if ($value['category'] == "CIRCUMFERENCE") {
                                $graph_array[$value['category']][] = implode(",", array($value['month'], $circumference, $value['m3sd'], $value['m1sd'], $value['sd'], $value['p1sd'], $value['p3sd'], $day));
